JS{
  const patch = @{var_patch};
  let fixture = patch.groups?.find(g => g.name == @{menu_fixture.value});
  if (fixture === undefined) {
    fixture = patch.instruments.find(i => i.name == @{menu_fixture.value});
  }

  return fixture;
}

JS{
  const instruments = @{var_patch.value.instruments};
  const params = instruments
    .map(i => window.Object.values(i.definition.parameters))
    .reduce((a, b) => a.concat(b));

  let uniqueParams = [];
  for (const p of params) {
    if (!uniqueParams.some(u => u.name == p.name)) {
      uniqueParams.push(p);
    }
  }

  return uniqueParams;
}

