widgets.props:
JS{
  const picker = @{rgb_picker.value};
  const fixture = @{var_fixture};
  let params = null;
  if (typeof fixture.definition !== 'undefined') {
    params = window.Object.values(fixture.definition.parameters).filter(p => p.type == 'scalar');
  } else {
    params = fixture.parameters.filter(p => p.type == 'scalar');
  }

  const param = params[$];

  let colorFill = 'rgb(0,0,0)';
  if (param.name == 'red') {
    colorFill = 'rgb(#_{@_{this} / 100 * 255},0,0)';
  } else if (param.name == 'green') {
    colorFill = 'rgb(0,#_{@_{this} / 100 * 255},0)';
  } else if (param.name == 'blue') {
    colorFill = 'rgb(0, 0, #_{@_{this} / 100 * 255})';
  } else if (param.name == 'intensity') {
    let incr = '#_{@_{this} / 100 * 255}';
    colorFill = `rgb(${incr},${incr},${incr})`;
  }

  return {
    linkId: `fader_${fixture.name}_${param.name}`,
    'default': 0,
    asYouType: true,
    numeric: true,
    unit: param.valueIsPercentage ? '%' : '',
    colorFill,
    css: ':focus { color: white; caret-color: white; }',
    decimals: 0,
  };
}

widgets.onValue:
if (value === 'menu') return;

set('this', 'menu');

if (value == 'interfaces') {
  set('modal_interfaces', 1);
  return;
}

if (value == 'create_preset') {
  set('var_mode', 'create_preset');
  return;
}

if (value == 'cancel_preset') {
  set('var_mode', 'normal');
  return;
}

if (value == '/dashboard/clear') {
  const fixture = get('var_fixture');
  let params = null;
  if (typeof fixture.definition !== 'undefined') {
    params = window.Object.values(fixture.definition.parameters);
  } else {
    params = fixture.parameters;
  }

  params = params.filter(p => p.type == 'scalar');

  for (const p of params) {
    console.log(p);
    set(`fader_${fixture.name}_${p.name}`, 0);
  }
}

send(value);

const fixture = get('var_fixture');

if (value === 1) {
  if (get('var_rgb_prop') !== '') {
    let rgbValues = [];

    for (const x of ['red', 'green', 'blue']) {
      const val = get(`fader_${fixture.name}_${x}`);
      rgbValues.push(val);
    }

    set('rgb_picker', rgbValues);
  }

  if (get('var_pt_prop') !== '') {
    let ptValues = [];

    for (const x of ['pan', 'tilt']) {
      const val = get(`fader_${fixture.name}_${x}`);
      ptValues.push(val);
    }

    set('xy_pantilt', ptValues);
  }
} else {
  if (get('var_rgb_prop') !== '') {
    let rgbValues = [];

    const [red, green, blue] = get('rgb_picker');
    set(`fader_${fixture.name}_red`, red);
    set(`fader_${fixture.name}_green`, green);
    set(`fader_${fixture.name}_blue`, blue);
  }

  if (get('var_pt_prop') !== '') {
    let ptValues = [];

    const [pan, tilt] = get('xy_pantilt');
    set(`fader_${fixture.name}_pan`, pan);
    set(`fader_${fixture.name}_tilt`, tilt);
  }
}

if (value === 1) {
  const workspaceId = get('workspace_id');
  let presets = storage.getItem(`${workspaceId}/presets`) || {};

  const params = get('var_patch_params');
  for (const p of params) {
    if (!Object.keys(presets).includes(p.name)) {
      presets[p.name] = [];
    }
  }

  set('var_presets', presets);
}

const key = get('var_preset_param');
const currentVal = get('var_presets');
set('var_presets', {
  ...currentVal,
  [key]: value,
});

