JS{
  const param = @{var_patch_params}[$];
  const name = param?.name ?? '';

  return {
    css: 'font-size: 15px; word-break: break-all;',
    label: name,
    value: name == @{var_preset_param} ? 1 : 0,
    onValue: `if (value === 1){set('var_preset_param', '${param?.name ?? 'other'}')}`,
    interaction: name != @{var_preset_param},
    wrap: true,
  };
}

JS{
  const mode = @{var_mode};
  const presets = @{var_active_presets};
  const preset = presets[$];
  const type = @{var_preset_param};
  const editing = @{var_preset_editing};

  return {
    label: preset?.name || (mode == 'create_preset' ? '+' : ''),
    mode: mode == 'edit_preset' ? 'toggle' : 'tap',
    interaction: mode == 'create_preset' || !!preset?.name,
    onValue: `const mode=get('var_mode');const index=${$};const fixtureName=get('var_fixture').name;if(mode=='create_preset'){const params=get('var_preset_create_params');if(params.length<1){return}let preset={name:'New Preset',values:{}};for(const p of params){preset.values[p]=get('fader_'+fixtureName+'_'+p)}let activePresets=get('var_active_presets');if(index>=activePresets.length){activePresets=[...activePresets,...new Array(index+1).fill({name:'',values:{}})]}const param=get('var_preset_param');activePresets[index]=preset;set('var_active_presets',activePresets);set('var_mode','edit_preset');set('var_preset_editing',param+'.'+index);return}else if(mode=='edit_preset'){if(value===1){set('var_preset_editing',get('var_preset_param')+'.'+index)}return}if(value===0){return}const activePresets=get('var_active_presets');const preset=activePresets[index];if(!preset){return}for(const [k,v] of Object.entries(preset.values)){set('fader_'+fixtureName+'_'+k,v)}set('modal_presets',0);`,
    css: 'margin: 2px;',
    value: mode == 'edit_preset' && editing == `${type}.${$}` ? 1 : 0,
  };
}

